const rawData = $('Redis').first().json.estado;
let data;

// üßπ Limpieza y manejo de nulos
if (!rawData) {
  // Si viene null o vac√≠a, devolvemos estructura base por defecto
  data = {
    Estado: "iniciando",
    Entradas: [
      {
        Entrada_inicial: "",
        Entrada_registro: "",
        Entrada_empresa: ""
      }
    ],
    Empresa: [
      {
        Nombre: "",
        Confianza_empresa: 0
      }
    ],
    Registro: [
      {
        Nombre: "",
        Confianza_registro: 0
      }
    ],
    Agente: [
      {
        Titulo: "",
        Descripci√≥n: "",
        Fecha: "",
        Departamento: "",
        Prioridad: ""
      }
    ]
  };

} else {
  // üßº Limpieza de caracteres problem√°ticos
  let cleaned = rawData
    .replace(/\n|\r|\t/g, ' ')        // elimina saltos, tabs y retornos
    .replace(/\\n|\\r|\\t/g, ' ')     // elimina versiones escapadas
    .replace(/‚Äú|‚Äù/g, '"')             // cambia comillas curvas
    .replace(/'/g, '"')               // reemplaza comillas simples por dobles
    .replace(/\s+/g, ' ')             // compacta espacios
    .trim();

  try {
    data = JSON.parse(cleaned);
  } catch (e) {
    // üß© fallback: intenta corregir claves sin comillas
    const fixed = cleaned.replace(/([{,]\s*)([A-Za-z0-9_]+)(\s*:\s*)/g, '$1"$2"$3');
    try {
      data = JSON.parse(fixed);
    } catch (err) {
      data = { error: 'No se pudo parsear el JSON', detalle: err.message, texto_original: rawData };
    }
  }
}

// ü™Ñ Agregar el campo extra desde el nodo Webhook
try {
  const Entrada_registro = $('Webhook').first().json.body.data.message.conversation;
  data.Entradas = data.Entradas || [{}];
  data.Entradas[0].Entrada_registro = Entrada_registro || "";
} catch (e) {
  if (!data.Entradas) data.Entradas = [{}];
  data.Entradas[0].Entrada_registro = "";
}

return data;

const raw = $('Redis1').first().json.data;
let data;

// üßπ Limpieza de caracteres problem√°ticos
let cleaned = raw
  .replace(/\n|\r|\t/g, ' ')        // elimina saltos, tabs y retornos
  .replace(/\\n|\\r|\\t/g, ' ')     // elimina versiones escapadas         / 
  .replace(/\s+/g, ' ')             // compacta espacios
  .trim();

try {
  data = JSON.parse(cleaned);
} catch (e) {
  // üß© fallback: intenta corregir manualmente si sigue fallando
  const fixed = cleaned.replace(/([{,]\s*)([A-Za-z0-9_]+)(\s*:\s*)/g, '$1"$2"$3');
  try {
    data = JSON.parse(fixed);
  } catch (err) {
    data = { error: 'No se pudo parsear el JSON', detalle: err.message, texto_original: raw };
  }

}

// ü™Ñ Agregar el campo extra desde el nodo Webhook
try {
  const Entrada_registro = $('Webhook').first().json.body.data.message.conversation;
  data.Entrada_registro = Entrada_registro;
} catch (e) {
  data.Entrada_registro = null; // fallback si el campo no existe
}

return data;

