// ð§  FunciÃ³n recursiva para recorrer estructuras anidadas de Notion
// (para separar entre EVENTOS y TAREAS segÃºn jerarquÃ­a)
function clasificarNotion(obj, nivel = 0, contexto = { eventos: [], tareas: {} }) {
  for (const key in obj) {
    const valor = obj[key];

    if (Array.isArray(valor)) {
      // Es una lista de tareas u objetos simples
      for (const tarea of valor) {
        const nombre = tarea.nombre || '(Sin nombre)';
        const estado = tarea.estado ? ` | ${tarea.estado}` : '';
        const prioridad = tarea.prioridad ? ` [${tarea.prioridad}]` : '';
        const horaInicio = tarea.inicio ? tarea.inicio.split('T')[1]?.slice(0,5) : '';
        const horaFin = tarea.fin ? tarea.fin.split('T')[1]?.slice(0,5) : '';
        const horario = horaInicio || horaFin ? ` (${horaInicio}-${horaFin})` : '';

        // Si estamos en el nivel raÃ­z, lo tomamos como evento
        if (nivel === 0) {
          contexto.eventos.push(`- ${nombre}${horario}`);
        } else {
          // Si estamos dentro de una empresa
          const empresaActual = contexto.empresa || 'Sin empresa';
          if (!contexto.tareas[empresaActual]) contexto.tareas[empresaActual] = [];
          contexto.tareas[empresaActual].push(`- ${nombre}${estado}${prioridad}`);
        }
      }
    } else if (typeof valor === 'object' && valor !== null) {
      // Quitamos @ si existe en nombre
      const nombreLimpio = key.startsWith('@') ? key.slice(1) : key;
      const subContexto = { ...contexto, empresa: nombreLimpio };
      clasificarNotion(valor, nivel + 1, subContexto);
    }
  }

  return contexto;
}

