  const horaFin = evento.fin?.split("T")[1]?.slice(0,5) || "";
  eventos.push(`- ${evento.nombre} (${horaInicio}-${horaFin})`);
}

// --- ð§¾ Armamos el texto final ---
if (eventos.length > 0) {
  mensaje += `*Eventos de hoy:*\n`;
