    mensaje += `ð¢ *${empresa}*\n`;
    mensaje += tareas.map(t => `   ${t}`).join("\n") + "\n\n";
  }
}

// Devolvemos el mensaje como salida Ãºnica
return [{ json: { mensaje } }];

